# include<bits/stdc++.h>
using namespace std;
int main()
{
    int n;
    cout<<"Please enter a no beteween 1 to 12:";
    cin>>n;
    if (n==1)
    cout<<"Month is January";
    else if (n==2)
    cout<<"Month is February";
    else if (n==3)
    cout<<"Month is March";
    else if (n==4)
    cout<<"Month is April";
    else if (n==5)
    cout<<"Month is May";
    else if (n==6)
    cout<<"Month is June";
    else if (n==7)
    cout<<"Month is July"; 
    else if (n==8)
    cout<<"Month is August";                   
    else if (n==9)
    cout<<"Month is September";
    else if (n==10)
    cout<<"Month is October";
    else if (n==11)
    cout<<"Month is November";           
    else if (n==12)
    cout<<"Month is December";
    else
    cout<<"Invalid no";
    return 0;
}

