
# include<bits/stdc++.h>
using namespace std;
int main()
{
    int num;
    cout<<"Enter a number between 0 to 5:";
    cin>>num;
    if (num==0)
    cout<<"Zero";
    else if (num==1)
    cout<<"One";
    else if (num==2)
    cout<<"Two";
    else if (num==3)
    cout<<"Three";
    else if (num==4)
    cout<<"Four";
    else if (num==5)
    cout<<"five";
    else
    cout<<"Erro, Number should be in range 0-5";
    return  0;
}
